import React, { useState } from "react";
import { Botao } from "../../components/button/style";
import Header from "../../components/header";
import {
  LeftContainer,
  LoadingIcon,
  PageContainer,
  RightContainer,
  Tabela,
} from "./style";

function Homepage() {
  const [isLoading, setIsLoading] = useState(false);
  const [retreinarResponse, setRetreinarResponse] = useState(false);

  function handleRetreinar() {
    setIsLoading(true);
  }

  function formatDate(dateString) {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, "0");
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const year = date.getFullYear();

    return `${day}/${month}/${year}`;
  }

  const respostaMock = {
    predicted_values: [
      {
        ds: "2024-05-28T00:00:00",
        yhat: 0.12823345787629414,
      },
      {
        ds: "2024-05-29T00:00:00",
        yhat: 0.12838014591399596,
      },
      {
        ds: "2024-05-30T00:00:00",
        yhat: 0.12808506207406295,
      },
      {
        ds: "2024-05-31T00:00:00",
        yhat: 0.1286978822132271,
      },
      {
        ds: "2024-06-01T00:00:00",
        yhat: 0.12794510423817118,
      },
      {
        ds: "2024-06-02T00:00:00",
        yhat: 0.1281001475441034,
      },
      {
        ds: "2024-06-03T00:00:00",
        yhat: 0.12860066324271557,
      },
      {
        ds: "2024-06-04T00:00:00",
        yhat: 0.1282800374600419,
      },
      {
        ds: "2024-06-05T00:00:00",
        yhat: 0.1284267254977464,
      },
      {
        ds: "2024-06-06T00:00:00",
        yhat: 0.12813164165781293,
      },
    ],
    lowest_value: 0.12794510423817118,
    highest_value: 0.1286978822132271,
  };

  return (
    <PageContainer>
      <Header />
      <LeftContainer>
        <Botao>Prever próximos 10 dias</Botao>
        <Tabela>
          <thead>
            <tr>
              <td>Data</td>
              <td>Preço</td>
            </tr>
          </thead>
          <tbody>
            {respostaMock?.predicted_values.map((item, index) => (
              <tr key={index + 1}>
                <td>{formatDate(item.ds)}</td>
                <td>{item.yhat}</td>
              </tr>
            ))}
          </tbody>
        </Tabela>
        <Tabela>
          <tr style={{ backgroundColor: "#24ABE3", color: "#fff" }}>
            <td>Venda</td>
            <td>
              {formatDate(
                respostaMock.predicted_values.find(
                  (item) => item.yhat === respostaMock.highest_value
                ).ds
              )}
            </td>
            <td>{respostaMock.highest_value}</td>
          </tr>
          <tr style={{ backgroundColor: "#0B8C38", color: "#fff" }}>
            <td>Compra</td>
            <td>
              {formatDate(
                respostaMock.predicted_values.find(
                  (item) => item.yhat === respostaMock.lowest_value
                ).ds
              )}
            </td>
            <td>{respostaMock.lowest_value}</td>
          </tr>
        </Tabela>
      </LeftContainer>
      <RightContainer>
        <Botao onClick={() => handleRetreinar}>Retreinar</Botao>
        {isLoading ? <LoadingIcon /> : null}
      </RightContainer>
    </PageContainer>
  );
}

export default Homepage;
